from flask import Flask

# app이라는 객체 생성
app = Flask(__name__)

# route 데코레이터 - URL 관리
@app.route("/")
# 뷰 함수 -> 화면 표시 함수
def hello():
    return "<h1 style='text-align:center'>Hello World!</h1>" \
           "<p><a href='/test'>test</a></p>" #텍스트, html 태그 가능

@app.route("/test/")
def test():
    return "test page"

@app.route("/gallery/")
def gallary():
    return "gallary page" \
           "<br>" \
           "<img src='data:image/jpeg;base64,'>"
if __name__ == "__main__":
    app.run()